namespace Magestore\Storepickup\Controller\Adminhtml\Store;

use Magento\Framework\Controller\ResultFactory;

/**
 * Index Grid Store Action.
 *
 * @category Magestore
 * @package  Magestore_Storepickup
 * @module   Storepickup
 * @author   Magestore Developer
 */
class Index extends \Magestore\Storepickup\Controller\Adminhtml\Store
{
    /**
     * Index action.
     *
     * @return \Magento\Framework\Controller\ResultInterface
     */
    public function execute()
    {
        /** @var \Magento\Backend\Model\View\Result\Page $resultPage */
        $resultPage = $this->resultFactory->create(ResultFactory::TYPE_PAGE);
        $this->initPage($resultPage);
        $resultPage->getConfig()->getTitle()->prepend(__('Manage Store'));

        return $resultPage;
    }
}
